import React, { useState, useEffect } from "react";
import styled from "styled-components";
import ImgComponent from "./ImageComponent";
import dummy from "./../db/restaurant.json";
import { useSelector } from "react-redux";
import ReviewScope from "./ReviewScope";
import restaurantModal from "../modules/restaurantModal";
import { RestaurantModalAPI } from "../api/RestaurantModalAPI";
import ReviewAPI from "../api/Review";
import { useInView } from "react-intersection-observer";

const RestaurantModal = (props) => {
  const [listData, setListData] = useState([]);
  const selectRestaurantId = useSelector((state) => state.restaurantModal.id);

  //무한 스크롤 : 라이브러리 react-intersection-observer
  const [ref, inView] = useInView();
  useEffect(() => {
    console.log(inView.toString());
  }, [ref, inView]);

  useEffect(() => {
    RestaurantModalAPI((data) => {
      setListData(data);
    }, selectRestaurantId);
  }, [selectRestaurantId]);

  if (listData === null) {
    return;
  }

  //매장 상세 정보
  // const selectRestaurantAdd = useSelector((state) => state.restaurantModal.add);
  // const selectRestaurantDB = listData.filter(
  //   (restaurant) =>
  //     restaurant.title === selectRestaurantName &&
  //     restaurant.add === selectRestaurantAdd
  // );

  // console.log(selectRestaurantId)
  // console.log(listData[selectRestaurantId-1])
  // const info = listData[selectRestaurantId - 1];
  // console.log(info.thumbnail)

  return (
    <RestaurantModalWrap>
      <CloseWrap>
        <Close onClick={props.detailModalClose}>
          <ImgComponent src={"close.png"} width={"100%"} />
        </Close>
      </CloseWrap>
      <Box>
        {/* <Back></Back>  */}
        {/* {restaurantDetail} */}
        <div key={listData.id}>
          <MainImg>
            <Thumbnail src={listData.thumbnail} width={"100%"} />
          </MainImg>
          <About>
            <Title>{listData.name}</Title>
            <Address>{listData.numberAddress}</Address>
            <ScopeWrap>
              <Scope>{listData.review_rating}</Scope>
              <ReviewScope scope={listData.review_rating} />
            </ScopeWrap>
          </About>
          <Info>
            <InfoComponent img={"time.png"} txt={listData.time} />
            <InfoComponent img={"link.png"} txt={listData.link} />
            <InfoComponent img={"phone.png"} txt={listData.number} />
          </Info>
        </div>
        <Divider></Divider>
        {/* <ReviewPage onClick={props.openAllReivew} /> */}
        <ReviewWrap onClick={props.openAllReivew}>
          <ReviewTxtWrap>
            <ReviewNum>리뷰 {listData.review_number}개</ReviewNum>
            <ReviewMore onClick={props.onClick}>더보기</ReviewMore>
          </ReviewTxtWrap>
          <ReviewFlexWrap>
            <ReviewList />
            <div ref={ref}></div>
          </ReviewFlexWrap>
        </ReviewWrap>
      </Box>
    </RestaurantModalWrap>
  );
};

const RestaurantModalWrap = styled.div`
  height: 85%;
  width: 100%;
  /* position: absolute;
    bottom: 0;
    left: 0;
    right: 0;
    */
  z-index: 20;
  position: absolute;
  left: 0;
  bottom: 0;
  border: 1px solid #c09567;
  border-radius: 10px;
  padding: 40px 22px;
  box-sizing: border-box;
  background-color: #fff;
  @media screen and (max-width: 1000px) {
    padding-top: 20px;
  }
`;

const Thumbnail = styled.img``;

const Box = styled.div`
  overflow: scroll;
  -ms-overflow-style: none;
