import React, { useState, useEffect } from "react";
import { useSelector } from "react-redux";
import { useInView } from "react-intersection-observer";
import styled from "styled-components";

import ImgComponent from "./ImageComponent";
import ReviewScope from "./ReviewScope";

import { RestaurantModalAPI } from "../api/RestaurantModalAPI";
import ReviewAPI from "../api/Review";

const RestaurantModal = (props) => {
  const [listData, setListData] = useState([]);
  const selectRestaurantId = useSelector((state) => state.restaurantModal.id);
  const showURL = useSelector((state) => state.urlChange.name);

  useEffect(() => {
    RestaurantModalAPI(
      (data) => {
        setListData(data);
      },
      selectRestaurantId,
      showURL
    );
  }, [selectRestaurantId, showURL]);

  if (listData === null) {
    return;
  }

  return (
    <RestaurantModalWrap>
      <CloseWrap>
        <Close onClick={props.detailModalClose}>
          <ImgComponent src={"close.png"} width={"100%"} />
        </Close>
      </CloseWrap>
      <Box>
        <div key={listData.id}>
          <MainImg>
            <Thumbnail src={listData.thumbnail} width={"100%"} />
          </MainImg>
          <About>
            <Title>{listData.name}</Title>
            <Address>{listData.numberAddress}</Address>
            <ScopeWrap>
              <Scope>{listData.review_rating}</Scope>
              <ReviewScope scope={listData.review_rating} />
            </ScopeWrap>
          </About>
          <Info>
            <InfoComponent img={"time.png"} txt={listData.time} />
            <InfoComponent img={"link.png"} txt={listData.link} />
            <InfoComponent img={"phone.png"} txt={listData.number} />
          </Info>
        </div>
        <Divider></Divider>
        <ReviewWrap onClick={props.openAllReivew}>
          <ReviewTxtWrap>
            <ReviewNum>리뷰 {listData.review_number}개</ReviewNum>
            <ReviewMore onClick={props.onClick}>더보기</ReviewMore>
          </ReviewTxtWrap>
          <ReviewList />
        </ReviewWrap>
      </Box>
    </RestaurantModalWrap>
  );
};

const RestaurantModalWrap = styled.div`
  height: 85%;
  width: 100%;
  z-index: 20;
  position: absolute;
  left: 0;
  bottom: 0;
  border: 1px solid #c09567;
  border-radius: 10px;
  padding: 40px 22px;
  box-sizing: border-box;
  background-color: #fff;
  @media screen and (max-width: 1000px) {
    padding-top: 20px;
  }
`;

const Thumbnail = styled.img``;

const Box = styled.div`
  overflow: scroll;
  -ms-overflow-style: none;
  scrollbar-width: none;
  height: 100%;
  padding-bottom: 50px;
  box-sizing: border-box;
  &::-webkit-scrollbar {
    display: none;
  }
`;
const CloseWrap = styled.div`
  &::after {
    display: block;
    content: "";
    line-height: 0;
    clear: both;
  }
  &::before {
    display: block;
    content: "";
    line-height: 0;
    clear: both;
  }
`;
const Close = styled.div`
  width: 17px;
  height: 17px;
  margin-bottom: 30px;
  float: right;
  margin-right: 1%;
  @media screen and (max-width: 1000px) {
    width: 12px;
    height: 12px;
    margin-right: 0;
    padding-right: 0;
  }
`;

const MainImg = styled.div`
  background-color: #c09567;
  border-radius: 10px;
  width: 80%;
  margin: auto;
  height: 300px;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  margin-bottom: 20px;
  @media screen and (max-width: 1000px) {
    height: 140px;
    width: 90%;
  }
`;
const About = styled.div`
  display: flex;
  align-items: center;
  justify-content: center;
  flex-direction: column;
`;
const Title = styled.p`
  font-weight: 700;
  font-size: 20px;
  color: #000000;
  margin: 0;
`;
const Address = styled.p`
  margin-top: 10px;
  margin-bottom: 20px;
  font-weight: 400;
  font-size: 12px;
  color: #999999;
